con:config.script:
// Create the XmlHolder object for the response of the request
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
//Get request
def reqHolder = groovyUtils.getXmlHolder( "Subtract#Request" )

//Modify the request parameters
reqHolder["//tem:intA"] = 100
reqHolder["//tem:intB"] = 80

log.info "Interget A: ${reqHolder['//tem:intA']}"
log.info "Interget B: ${reqHolder['//tem:intB']}"

//Update request
reqHolder.updateProperty()

//Run the test step
testRunner.runTestStepByName("Subtract")

//Get response
def resHolder = groovyUtils.getXmlHolder( "Subtract#Response" )

//Print response value
log.info "Result: ${resHolder['//ns1:SubtractResult']}"

